  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"guide"}}>{`Guide`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"creating-a-dumb-component"}}>{`Creating a Dumb Component`}</MDXTag>
<MDXTag name="p" components={components}>{`Dumb Component, Presentational Component or (sometimes) Controlled Component is a component
responsible `}<MDXTag name="strong" components={components} parentName="p">{`only for displaying content without any logic behind`}</MDXTag>{`. Usually they
receive specific props, and if they are interactive, it exposes events like onClick, onChange, etc.`}</MDXTag>
<MDXTag name="p" components={components}>{`A Styled Component a good example.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx"}}>{`import React from "react";
import styled from "styled-components";

const DumbCheckbox = styled("div")\`
  cursor: pointer;
  &:before {
    content: '\${props => (props.checked ? "■" : "□")} ';
  }
\`;

const App = () => (
  <DumbCheckbox>Check me</DumbCheckbox>
);
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"using-react-powerplug"}}>{`Using React PowerPlug`}</MDXTag>
<MDXTag name="p" components={components}>{`Now that you have your Dumb Component, you can pass state to it.`}<MDXTag name="br" components={components} parentName="p"></MDXTag>
{`Using react-powerplug this step is trivial and pretty simple.  `}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx"}}>{`import { Toggle } from "react-powerplug";

<Toggle>
  {({ on, toggle }) => (
    <DumbCheckbox checked={on} onClick={toggle}>
      Check me
    </DumbCheckbox>
  )}
</Toggle>
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
